DELIMITER $$

--
-- Create trigger `act_comandas_au`
--
CREATE 
	DEFINER = 'root'@'localhost'
TRIGGER IF NOT EXISTS act_comandas_au
	BEFORE INSERT
	ON comandas
	FOR EACH ROW
begin
	
	set new.fecha = curdate();
SET new.precio_total = (SELECT DISTINCT
    (new.cantidad * (SELECT
        precio_publico
      FROM platos
      WHERE platos.id = new.id_plato))
  FROM comandas);

end
$$

